mapper.update:
update stock_tb
set
    current_stock = #{currentStock},
    expected_stock = #{expectedStock}
where
    id = #{id}

update stock_tb
set
    min_alert_quantity = #{minAlertQuantity},
    alert_setting = #{alertSetting}
where
    id = #{id}

update.if:
update stock_tb
set
    current_stock = current_stock - #{productCount}
where
    product_id = #{productId}

update stock_tb
set
    current_stock = current_stock + #{productCount},
    expected_stock = expected_stock + #{productCount}
where
    product_id = #{productId}

mapper.select:
select product_id as productId, current_stock as currentStock from stock_tb where product_id in (
#{productId}
)
